        {items.map((item, i) => (
            <div key={i} style={{ display: 'flex', justifyContent: 'space-between', marginTop: '8px', }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <div style={{
                        width: '12px',
                        height: '12px',
                        borderRadius: '50%',
                        marginRight: '8px',
                        background: item.selected ? '#0af' : '#888'
                    }}></div>
                    <span>{item.name}</span>
                </div>
                <span style={{
                    fontWeight: 700,
                    fontStretch: "bold",

                }}>{item.price}</span>
            </div>
        ))}
